  // --- TRACE Framework ---
  trace_name: "Task, Request, Action, Context, Example",
  trace_shortName: "TRACE",
  trace_description_updated: "TRACE is a comprehensive framework that breaks down a prompt into: Task (overall job), Request (specific need), Action (AI's operation), Context (background), and Example (desired output style). It's designed for clarity and precision, especially when the request is multifaceted.",
  trace_short_desc_updated: "Precision: Task, Request, Action, Context, Example.",
  trace_comp1_label: "Task:",
  trace_comp1_example_updated: "Summarize a research paper.",
  trace_task_tooltip_updated: "Define the general category of work to be done.",
