  crispe_experiment_tooltip_updated: "Suggest a method, process, or approach the AI should take to complete the task.",

  // --- APE Framework ---
  ape_name: "Action, Purpose, Expectation",
  ape_shortName: "APE",
  ape_description_updated: "The APE framework is a concise yet effective way to structure prompts. It requires defining the Action the AI should perform, the Purpose behind that action, and the Expectation for the output. This clarity helps in getting focused and relevant responses.",
  ape_short_desc_updated: "Focused: Action, Purpose, Expectation.",
  ape_comp1_label: "Action:",
  ape_comp1_example_updated: "Generate a list of 5 blog post ideas.",
  ape_action_tooltip_updated: "Clearly state the specific task or operation the AI should perform.",
