  scenecraft_mood_lighting_tooltip: "Specify the dominant mood (e.g., tense, joyful, eerie) and describe the lighting conditions (sources, quality, color).",

  // --- Midjourney Framework ---
  midjourney_name: "Image Generation",
  midjourney_shortName: "Midjourney",
