  midjourney_comp_aspect_ratio_example_updated: "--ar 16:9",
  midjourney_aspect_ratio_tooltip_updated: "Sets the width-to-height ratio (e.g., 16:9 for landscape, 2:3 for portrait, 1:1 for square).",
