  midjourney_tile_tooltip_updated: "Generates images that can be used as repeating patterns.",
  midjourney_comp_image_weight_label: "Image Weight (--iw):",
  midjourney_comp_image_weight_example_updated: "--iw 0.5",
  midjourney_image_weight_tooltip_updated: "Sets the influence of an image prompt versus text (0-2). Default is 1.",
